import React from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  FlatList,
  Image, // Aseg√∫rate de que Image est√© importado
} from 'react-native';
import { useGame } from '../context/GameContext';

export default function MainGameScreen({ navigation }) {
  const { objects, setCurrentObject, score, isGameComplete } = useGame();

  React.useEffect(() => {
    if (isGameComplete()) {
      navigation.navigate('GameOver');
    }
  }, [objects, navigation, isGameComplete]);

  const handleObjectPress = (object) => {
    if (!object.found) {
      setCurrentObject(object);
      navigation.navigate('Camera');
    }
  };

  const renderObject = ({ item }) => (
    <TouchableOpacity
      style={[
        styles.objectCard,
        item.found && styles.foundObjectCard
      ]}
      onPress={() => handleObjectPress(item)}
      disabled={item.found}
    >
      <Image source={require(`../imag/${item.image}`)} style={styles.objectImage} resizeMode="contain" />
      <Text style={styles.objectName}>{item.name}</Text>
      {item.found && (
        <View style={styles.foundIndicator}>
          <Text style={styles.foundIcon}>üëÅÔ∏è</Text>
        </View>
      )}
    </TouchableOpacity>
  );

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Encuentra los objetos</Text>
        <Text style={styles.score}>Puntos: {score}</Text>
      </View>

      <View style={styles.content}>
        <FlatList
          data={objects}
          renderItem={renderObject}
          keyExtractor={(item) => item.id.toString()}
          numColumns={2}
          contentContainerStyle={styles.objectsList}
          showsVerticalScrollIndicator={false}
        />
      </View>

      <View style={styles.pagination}>
        <View style={styles.dot} />
        <View style={styles.dot} />
        <View style={[styles.dot, styles.activeDot]} />
        <View style={styles.dot} />
        <View style={styles.dot} />
        <View style={styles.dot} />
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#ffffff',
    margin: 20,
    borderRadius: 24,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 8,
    elevation: 5,
  },
  header: {
    paddingHorizontal: 24,
    paddingTop: 24,
    paddingBottom: 16,
    alignItems: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: '500',
    color: '#000000',
    marginBottom: 8,
  },
  score: {
    fontSize: 16,
    color: '#312b2b',
  },
  content: {
    flex: 1,
    paddingHorizontal: 24,
  },
  objectsList: {
    paddingVertical: 16,
  },
  objectCard: {
    flex: 1,
    aspectRatio: 1,
    backgroundColor: '#d9d9d9',
    borderRadius: 12,
    margin: 8,
    justifyContent: 'center',
    alignItems: 'center',
    position: 'relative',
  },
  objectCard: {
    width: '48%',
    aspectRatio: 1,
    backgroundColor: '#d9d9d9',
    borderRadius: 12,
    margin: 8,
    justifyContent: 'center',
    alignItems: 'center',
    position: 'relative',
  },
  objectIcon: {
    fontSize: 32,
    marginBottom: 8,
  },
  objectName: {
    fontSize: 14,
    fontWeight: '500',
    color: '#000000',
    textAlign: 'center',
  },
  foundIndicator: {
    position: 'absolute',
    top: 8,
    right: 8,
    backgroundColor: '#4caf50',
    borderRadius: 12,
    width: 24,
    height: 24,
    justifyContent: 'center',
    alignItems: 'center',
  },
  foundIcon: {
    fontSize: 12,
  },
  pagination: {
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    paddingBottom: 24,
    gap: 8,
  },
  dot: {
    width: 8,
    height: 8,
    borderRadius: 4,
    backgroundColor: '#d9d9d9',
  },
  activeDot: {
    backgroundColor: '#000000',
  },
  objectImage: { // <- Aqu√≠ est√° el estilo que agregamos
    width: 80,
    height: 80,
    resizeMode: 'contain',
  },
});
